  (() => {
    // This references the final, complete distilled summary object from the evaluator loop.
    // Note: Update the node name if it's different. I'm assuming the data comes from the "IF: Output Exists" node's true path.
    const summaryObject = $('IF: evaluationLoop < maxEvaluations').item.json.distilledSummary;

    if (!summaryObject) return "No research summary available.";

    // The JSON from the state might be a string, so we must parse it.
    const knowledge = (typeof summaryObject === 'string') ? JSON.parse(summaryObject) : summaryObject;

    const summarySection = `## Key Insights & Synthesis\n${knowledge.distilledSummary || ''}\n\n`;
    const factsSection = '## Cited Findings\n' + (knowledge.citedFacts || []).map(item => `- ${item.fact} ${(item.sources || []).map(url => `<${url}>`).join(' ')}`).join('\n') + '\n\n';
    const referencesSection = '## Source List\n' + (knowledge.collatedReferences || []).map(url => `- <${url}>`).join('\n');

    return summarySection + factsSection + referencesSection;
  })()
}}

