={{
/**
 * Build the full historical ResearchLog for the Distiller and
 * immediately return the final prompt string for the model.
 */
(() => {

  // --- 1. Pull previous raw log ---------------------------
  const oldLog = $('prepareInput')?.item?.json?.ResearchLog || '';

  // --- 2. Pull NEW findings produced in this loop ----------
  const newFindings = $('Aggregate')?.item?.json?.researchFindings || [];

  // --- 3. Render those new findings to text ----------------
  const newLogPart = newFindings.map((f, i) => `--- Research Result ---
Query: "${f.query || 'No query'}"
Result: ${f.result || 'No result'}

Sources:
${
  (f.citations || []).length
    ? f.citations.map((c, i) => `- Source ${i + 1}: <${c}>`).join('\n')
    : 'No sources provided.'
}
---`).join('\n\n');

  // --- 4. Stitch old + new together ------------------------
  const fullHistoricalLog = [oldLog, newLogPart].filter(Boolean).join('\n\n');

  // --- 5. Emit the final user prompt -----------------------
  return `Below is the complete raw ResearchLog for distillation. Follow your system instructions and output ONLY the JSON object.

${fullHistoricalLog}
`;

})();
}}
